$(document).ready(function(){$("#product_taxon_ids").length>0&&$("#product_taxon_ids").select2({placeholder:Spree.translations.taxon_placeholder,multiple:!0,initSelection:function(e,t){return url=Spree.url(Spree.routes.taxons_search,{ids:e.val()}),$.getJSON(url,null,function(e){return t(e)})},ajax:{url:Spree.routes.taxons_search,datatype:"json",data:function(e){return{q:{name_cont:e}}},results:function(e){return{results:e}}},formatResult:function(e){return e.pretty_name},formatSelection:function(e){return e.pretty_name}})});